    // height: 100%;
    height: 500px;
    background-color: light-gray;
    // right:0;
    // top:0; 
`




let EventView = props => {
    console.log(props.context);

    const toggleMenu = () => {
        props.context.update({
            isAsideOpen: !props.context.isAsideOpen
        })
    }

    return (
        <>
            <h1>Event test</h1>
            <button
                className={"bg-blue-300 p-5"}
                onClick={toggleMenu}
            >menu</button>
            <SideMenu />
        </>

    )
}

EventView = withContext(EventView);

export default EventView;
